public class SnailSolution
{
   public static int[] Snail(int[][] array)
   {
       var solution = new List<int>();
     if (array[0].Length == 0) return new int[0];
     var rowBegin = 0;
     var columnBegin = 0;
     var rowEnd = array.Length - 1;
     var columnEnd = array.Length - 1;

     while(rowBegin <= rowEnd && columnBegin <= columnEnd)
     {
       for(var i = columnBegin; i <= columnEnd; i++){
         solution.Add(array[rowBegin][i]);
      }
      rowBegin++;

      for(var i = rowBegin; i <= rowEnd; i++){
          solution.Add(array[i][columnEnd]);
      }
      columnEnd--;


         for(var i = columnEnd; i >= columnBegin; i--){
            solution.Add(array[rowEnd][i]);
         }      

      rowEnd--;


          for(var i = rowEnd; i>= rowBegin; i--){
             solution.Add(array[i][columnBegin]);
          }          

      columnBegin++;

       }
     return solution.ToArray();
   }
}

