public static class JadenCase
{
  public static string ToJadenCase(this string phrase)
  {
    return string.Join(" ", phrase.Split().Select(word => TitleCase(word)));
  }

  private static string TitleCase(string word) => word[0].ToString().ToUpper() + word.Substring(1);
}using System;
using System.Collections.Generic;
public static class JadenCase
{
  public static string ToJadenCase(this string phrase)
  {
    var JadenCasedString = new List<string>();
    foreach (var word in phrase.Split())
      {
        JadenCasedString.Add(word.Substring(0, 1).ToUpper() + word.Substring(1, word.Length-1));
      }

    return String.Join(" ", JadenCasedString);
  }
}

